import React from "react";
//import { Map, Marker, Popup, TileLayer, Polyline } from "react-leaflet";
//import { Icon } from "leaflet";
//import * as parkData from "./data/skateboard-parks.json";
import "./App.css";
import MapaMundi from "./Map.js"
import Planes from "./Planes.js";
import MyForm from "./Chat.js";
//import Planes from "./Planes"

const io = require("socket.io-client")
const socket = io.connect('ws://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
    path:'/flights'
});

export default class App extends React.Component {

  constructor(props) {
    super(props);
    this.state= {
      flights: [],
      userName: "",
      isLogged: false,
      messages: [],
      position: []
    }
  }

  componentDidMount(){
    socket.on("POSITION", position => {
      if(this.state.position.some(e => e.code === position.code)) {
        let index = this.state.position.indexOf(position.code);
        let new_position = this.state.position
        //Me falta hacer el .setState
        console.log(new_position)
        new_position[index]=position;
        this.setState({ position: new_position })
      } else
        this.setState((state) => ({ position: [...state.position, position]}))
        //this.state.position.push(position)
    })
  }

  getInfo = (e) => {
    e.preventDefault();
    socket.emit('FLIGHTS',"");
    socket.on('FLIGHTS', message => {
      console.log(message);
      this.setState({ flights: message })
    })
  }

  render () {
    return(
      <div>
        <MapaMundi
        flights= {this.state.flights}
        position= {this.state.position}

        />
        <Planes
        getInfo= {this.getInfo}
        flights= {this.state.flights}
        />
        <MyForm/>
      </div>

    )
  }
}
