import ReactDOM from 'react-dom';
import React, { Component } from 'react';

//import { LatLngExpression } from "leaflet";

var socket = io.connect('http://localhost:3000', {'forceNew':true});

//var socket = io.connect('wss://tarea-3-websocket.2021-1.tallerdeintegracion.cl', 
//{path:'/position'});


state = {
    userName:'Carlos',
    isLogged: false,
    messages:[]
}

socket.on('chat-message', function(data){
    state.messages.push(data);
    console.log(data);
    console.log(state.isLogged);
    render(state.messages);

});


function render(data) {

    var color = '#5faee3'
    var html = data.map(function(elem, index){
        return( `<div style='color:${color}'>
                    <strong>${elem.name}</strong>:
                    <em>${elem.message}</em>
                    <h5>${elem.date}</h5>
                </div>`);
    }).join(" ");

    document.getElementById('messages').innerHTML = html;
};



function addMessage(e) {
    /*var logged = document.getElementById("username").value
    if (logged!='') {
        state.isLogged=true
    };*/
    var payload = {
        name: state.userName,
        message: document.getElementById("texto").value
    }

    socket.emit('new-chat-message',payload);
    return false;

}


