import React from "react";
//import { Map, Marker, Popup, TileLayer, Polyline } from "react-leaflet";
//import { Icon } from "leaflet";
//import * as parkData from "./data/skateboard-parks.json";
//import "./App.css";

/*export const icon = new Icon({
  iconUrl: "/skateboarding.svg",
  iconSize: [25, 25]
});*/
const io = require("socket.io-client")
const socket = io.connect('ws://tarea-3-websocket.2021-1.tallerdeintegracion.cl', {
    path:'/flights'
});

export default class Planes extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      flights: []
    }
    this.getInfo = this.getInfo.bind(this)
  };

  componentDidMount(){
    socket.on('FLIGHTS', function(data){
      console.log(data);});
  }

  getInfo(e) {
    e.preventDefault();
    socket.emit("FLIGHTS","")
    socket.on('FLIGHTS', message => {
      console.log(message)
      this.setState({ flights: message})
    })


  };

  render() {
    const information_flight =this.state.flights.map(flight=>{
      return  <div className="col-3">
                <div className="card card-block card-1">
                  <br></br>
                  <strong>{flight.airline}</strong>
                  {flight.plane}<br></br><br></br>
                  Origen: ({Math.round(flight.origin[0])}, {Math.round(flight.origin[1])})
                  Destino: ({Math.round(flight.destination[0])}, {Math.round(flight.destination[1])})
                  Asientos: {flight.seats}
                  Pasajeros: {flight.passengers}
                </div>
              </div>
            })

    return (

    <div>

      <div className="container-fluid">  
        <div className="scrolling-wrapper row flex-row flex-nowrap mt-4 pb-4 pt-2">
          <div className="col-3">
            <button onClick={this.getInfo}>Get Info</button>
          </div>
          {information_flight}  
        </div>
      </div>

    </div>)

  }
}
