import React from "react";


export default class Planes extends React.Component {
  constructor(props) {
    super(props);
  };


  render() {
    const information_flight =this.props.flights.map(flight=>{
      return  <div className="col-3">
                <div className="card card-block card-1" style={{paddingLeft: '13px'}}>
                  <br></br>
                  <strong>{flight.airline}</strong>
                  {flight.plane}<br></br><br></br>
                  Origen: ({Math.round(flight.origin[0])}, {Math.round(flight.origin[1])})<br></br>
                  Destino: ({Math.round(flight.destination[0])}, {Math.round(flight.destination[1])})<br></br>
                  Asientos: {flight.seats}<br></br>

                  <div class="dropup" style={{padding:"3px"}}>
                    <button type="button" class="btn btn-info dropdown-toggle" data-toggle="dropdown">
                      Pasajeros
                    </button>
                    <div class="dropdown-menu">
                      {flight.passengers.map(pasajero=>
                        <a class="dropdown-item">{pasajero.name} : {pasajero.age}</a>
                      )}
                    </div>
                  </div>

                </div>
              </div>
            })

    return (

    <div>

      <div className="container-fluid">  
        <div className="scrolling-wrapper row flex-row flex-nowrap mt-4 pb-4 pt-2">
          <div className="col-3">
          <button onClick={this.props.quitInfo} class="btn btn-info shadow active lg-3" role="button" aria-pressed="false" style={{marginTop:"0", color:"black"}} >Ocultar Información</button>
            <button onClick={this.props.getInfo} class="btn btn-info shadow active lg-3" role="button" aria-pressed="true" style={{marginTop:"10%", color:"black"}} ><strong>OBTENER INFORMACIÓN</strong></button>
            <br></br><br></br><p style={{color:"rgb(143, 143, 143)"}}>Para ver más información de los vuelos, haz scroll hacia la derecha.</p>
          </div>
          {information_flight}  
        </div>
      </div>

    </div>)

  }
}
