import React from 'react';
import io from 'socket.io-client';
import { Card, Avatar, Input, Typography } from 'antd';
import 'antd/dist/antd.css';

const { Search } = Input;
const { Text } = Typography;
const { Meta } = Card;

var socket = io.connect('http://localhost:3000', {'forceNew':true});

export default class MyForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        userName: 'Carlos',
        messages: [],
    }
  }

  componentDidMount () {
    this.socket = io('/')
    this.socket.on('chat-message', message => {
        this.setState({ messages: [...this.state.messages, message]})
    })
  }

  onButtonClicked = (value) => {
    const payload = {
        message: value, 
        name: this.state.userName
    }
    this.setState({ messages: [...this.state.messages, payload]})
    this.socket.emit('new-chat-message', payload)
    this.setState({ searchVal: '' })

  }

  render() {

    return(

        <div>

            <div>

                <div className="title">
                    <Text id="main-heading" type="secondary" style={{ fontSize: '36px' }}>Websocket Chat: {this.state.userName}</Text>
                </div>

                <div style={{ display: 'flex', flexDirection: 'column', paddingBottom: 50}} id="messages" className="messages">
                    {this.state.messages.map(message => 
                    <Card key={message.message} style={{ width: 300, margin: '16px 4px 0 4px', alignSelf: this.state.userName === message.name ? 'flex-end' : 'flex-start' }} loading={false}>
                        <Meta
                        avatar={
                            <Avatar style={{ color: '#f56a00', backgroundColor: '#fde3cf' }}>{message.name[0].toUpperCase()}</Avatar>
                        }
                        title={message.name+":"}
                        description={message.message}
                        />
                    </Card> 
                    )}
                </div>

                <div className="bottom">
                    <Search
                    placeholder="input message and send"
                    enterButton="Send"
                    value={this.state.searchVal}
                    size="large"
                    onChange={(e) => this.setState({ searchVal: e.target.value })}
                    onSearch={value => this.onButtonClicked(value)}
                    />
                </div> 

            </div>
        </div>
    )
  }
}
